SELECT MIN(lt.link) AS link_type, MIN(t.title) AS first_movie, MIN(t3.title) AS second_movie FROM keyword AS k, link_type AS lt, movie_keyword AS mk, movie_link AS ml, title AS t, title AS t3 WHERE k.keyword ='character-name-in-title' AND mk.keyword_id = k.id AND t.id = mk.movie_id AND ml.movie_id = t.id AND ml.linked_movie_id = t3.id AND lt.id = ml.link_type_id AND mk.movie_id = t.id;