SELECT MIN(n.name) AS cast_member_name, MIN(pi.info) AS cast_member_info FROM aka_name AS an, cast_info AS ci, info_type AS it, link_type AS lt, movie_link AS ml, name AS n, person_info AS pi, title AS t WHERE n.id = an.person_id AND n.id = pi.person_id AND ci.person_id = n.id AND t.id = ci.movie_id AND ml.linked_movie_id = t.id AND lt.id = ml.link_type_id AND it.id = pi.info_type_id AND pi.person_id = an.person_id AND pi.person_id = ci.person_id AND an.person_id = ci.person_id AND ci.movie_id = ml.linked_movie_id AND an.name = 'Muñoz, Giselle Angelica' AND an.name = 'Muñoz, Giselle Angelica' AND an.name = 'Muñoz, Giselle Angelica' AND it.info = 'LD official retail price' AND lt.link = 'remade as' AND n.name_pcode_cf = 'H6326' AND n.gender = 'm' AND n.gender = 'm' AND n.name = 'Holton, Carter' AND pi.note = 'I.S.Mowis' AND t.production_year < 2010