SELECT MIN(cn.name) AS from_company, MIN(mc.note) AS production_note, MIN(t.title) AS movie_based_on_book FROM company_name AS cn, company_type AS ct, keyword AS k, link_type AS lt, movie_companies AS mc, movie_keyword AS mk, movie_link AS ml, title AS t WHERE lt.id = ml.link_type_id AND ml.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND t.id = mc.movie_id AND mc.company_type_id = ct.id AND mc.company_id = cn.id AND ml.movie_id = mk.movie_id AND ml.movie_id = mc.movie_id AND mk.movie_id = mc.movie_id AND cn.country_code = '[nr]' AND cn.name = 'Team De DoDoDo' AND cn.name = 'Team De DoDoDo' AND ct.kind = 'special effects companies' AND ct.kind = 'special effects companies' AND k.keyword = 'stealing-a-chicken' AND mc.note = '(1997) (USA) (VHS) (on "Bucky Beaver''s Dragon Art Theatre Triple XXX-Rated Double Feature Vol. 29")' AND t.production_year < 1910