SELECT MIN(cn.name) AS from_company, MIN(mc.note) AS production_note, MIN(t.title) AS movie_based_on_book FROM company_name AS cn, company_type AS ct, keyword AS k, link_type AS lt, movie_companies AS mc, movie_keyword AS mk, movie_link AS ml, title AS t WHERE lt.id = ml.link_type_id AND ml.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND t.id = mc.movie_id AND mc.company_type_id = ct.id AND mc.company_id = cn.id AND ml.movie_id = mk.movie_id AND ml.movie_id = mc.movie_id AND mk.movie_id = mc.movie_id AND cn.country_code = '[tr]' AND cn.name = 'Republic Distributing Corporation' AND cn.name = 'Republic Distributing Corporation' AND ct.kind = 'special effects companies' AND ct.kind = 'special effects companies' AND k.keyword = 'kidney-heist' AND mc.note = '(2010) (Non-USA) (TV) (Latin America)' AND t.production_year < 1960