SELECT MIN(n.name) AS voicing_actress, MIN(t.title) AS voiced_movie FROM aka_name AS an, char_name AS chn, cast_info AS ci, company_name AS cn, info_type AS it, movie_companies AS mc, movie_info AS mi, name AS n, role_type AS rt, title AS t WHERE t.id = mi.movie_id AND t.id = mc.movie_id AND t.id = ci.movie_id AND mc.movie_id = ci.movie_id AND mc.movie_id = mi.movie_id AND mi.movie_id = ci.movie_id AND cn.id = mc.company_id AND it.id = mi.info_type_id AND n.id = ci.person_id AND rt.id = ci.role_id AND n.id = an.person_id AND ci.person_id = an.person_id AND chn.id = ci.person_role_id AND ci.note = '(series director of photography)' AND cn.country_code = '[br]' AND it.info = 'screenplay-teleplay' AND mc.note = '(2007) (China) (video) (VOD/IPTV)' AND mc.note = '(1988) (Finland) (TV)' AND mc.note = '(1981) (Japan) (all media)' AND mi.info = 'USA:13 July 1940' AND mi.info = 'Denmark:7 March 2002' AND mi.info = 'UK:25 July 1965' AND n.gender = 'f' AND n.name = 'Matthee, Debbie' AND rt.role = 'production designer' AND t.production_year < 1978