SELECT MIN(cn.name) AS movie_company, MIN(mi_idx.info) AS rating, MIN(t.title) AS western_violent_movie FROM company_name AS cn, company_type AS ct, info_type AS it1, info_type AS it2, keyword AS k, kind_type AS kt, movie_companies AS mc, movie_info AS mi, movie_info_idx AS mi_idx, movie_keyword AS mk, title AS t WHERE kt.id = t.kind_id AND t.id = mi.movie_id AND t.id = mk.movie_id AND t.id = mi_idx.movie_id AND t.id = mc.movie_id AND mk.movie_id = mi.movie_id AND mk.movie_id = mi_idx.movie_id AND mk.movie_id = mc.movie_id AND mi.movie_id = mi_idx.movie_id AND mi.movie_id = mc.movie_id AND mc.movie_id = mi_idx.movie_id AND k.id = mk.keyword_id AND it1.id = mi.info_type_id AND it2.id = mi_idx.info_type_id AND ct.id = mc.company_type_id AND cn.id = mc.company_id AND cn.country_code = '[tr]' AND it1.info = 'sound mix' AND it2.info = 'sound mix' AND k.keyword = 'kidney-heist' AND kt.kind = 'tv movie' AND mc.note = '(2013) (Netherlands) (TV) (HBO3) (limited)' AND mc.note = '(2011) (Germany) (video)' AND mi.info = 'Mammoth skyscrapers of stone thundering across the earth!' AND mi_idx.info = '0..0201002' AND t.production_year < 1960