SELECT MIN(chn.name) AS voiced_char, MIN(n.name) AS voicing_actress, MIN(t.title) AS voiced_animation FROM aka_name AS an, complete_cast AS cc, comp_cast_type AS cct1, comp_cast_type AS cct2, char_name AS chn, cast_info AS ci, company_name AS cn, info_type AS it, info_type AS it3, keyword AS k, movie_companies AS mc, movie_info AS mi, movie_keyword AS mk, name AS n, person_info AS pi, role_type AS rt, title AS t WHERE t.id = mi.movie_id AND t.id = mc.movie_id AND t.id = ci.movie_id AND t.id = mk.movie_id AND t.id = cc.movie_id AND mc.movie_id = ci.movie_id AND mc.movie_id = mi.movie_id AND mc.movie_id = mk.movie_id AND mc.movie_id = cc.movie_id AND mi.movie_id = ci.movie_id AND mi.movie_id = mk.movie_id AND mi.movie_id = cc.movie_id AND ci.movie_id = mk.movie_id AND ci.movie_id = cc.movie_id AND mk.movie_id = cc.movie_id AND cn.id = mc.company_id AND it.id = mi.info_type_id AND n.id = ci.person_id AND rt.id = ci.role_id AND n.id = an.person_id AND ci.person_id = an.person_id AND chn.id = ci.person_role_id AND n.id = pi.person_id AND ci.person_id = pi.person_id AND it3.id = pi.info_type_id AND k.id = mk.keyword_id AND cct1.id = cc.subject_id AND cct2.id = cc.status_id AND cct1.kind = 'complete+verified' AND cct2.kind = 'complete+verified' AND ci.note = '(as Brian Lucero)' AND cn.country_code = '[tr]' AND it.info = 'sound mix' AND it3.info = 'sound mix' AND k.keyword = 'kidney-heist' AND mi.info = 'Rod Edgar. "Gate Ahead". In: "Dreamwatch 10pp" (USA), Titan Publishing Group, Iss. 97, (MG)' AND mi.info = 'Rod Edgar. "Gate Ahead". In: "Dreamwatch 10pp" (USA), Titan Publishing Group, Iss. 97, (MG)' AND mi.info = 'Rod Edgar. "Gate Ahead". In: "Dreamwatch 10pp" (USA), Titan Publishing Group, Iss. 97, (MG)' AND n.gender = 'f' AND n.name = 'Pick-Crystal, Sasha' AND rt.role = 'editor' AND t.production_year < 1960