SELECT MIN(mi.info) AS movie_budget, MIN(mi_idx.info) AS movie_votes, MIN(n.name) AS writer, MIN(t.title) AS violent_liongate_movie FROM cast_info AS ci, company_name AS cn, info_type AS it1, info_type AS it2, keyword AS k, movie_companies AS mc, movie_info AS mi, movie_info_idx AS mi_idx, movie_keyword AS mk, name AS n, title AS t WHERE t.id = mi.movie_id AND t.id = mi_idx.movie_id AND t.id = ci.movie_id AND t.id = mk.movie_id AND t.id = mc.movie_id AND ci.movie_id = mi.movie_id AND ci.movie_id = mi_idx.movie_id AND ci.movie_id = mk.movie_id AND ci.movie_id = mc.movie_id AND mi.movie_id = mi_idx.movie_id AND mi.movie_id = mk.movie_id AND mi.movie_id = mc.movie_id AND mi_idx.movie_id = mk.movie_id AND mi_idx.movie_id = mc.movie_id AND mk.movie_id = mc.movie_id AND n.id = ci.person_id AND it1.id = mi.info_type_id AND it2.id = mi_idx.info_type_id AND k.id = mk.keyword_id AND cn.id = mc.company_id AND ci.note = '(as Brian Lucero)' AND cn.name = 'Republic Distributing Corporation' AND it1.info = 'sound mix' AND it2.info = 'sound mix' AND k.keyword = 'kidney-heist' AND mc.note = '(2008) (2009) (Finland) (TV)' AND mi.info = 'Rod Edgar. "Gate Ahead". In: "Dreamwatch 10pp" (USA), Titan Publishing Group, Iss. 97, (MG)' AND n.gender = 'f' AND t.production_year < 1960 AND t.title = 'Illan Kajo: Herrain metkuja' AND t.title = 'Illan Kajo: Herrain metkuja' AND t.title = 'Illan Kajo: Herrain metkuja'