SELECT MIN(t.title) AS american_vhs_movie FROM company_type AS ct, info_type AS it, movie_companies AS mc, movie_info AS mi, title AS t WHERE t.id = mi.movie_id AND t.id = mc.movie_id AND mc.movie_id = mi.movie_id AND ct.id = mc.company_type_id AND it.id = mi.info_type_id AND ct.kind = 'special effects companies' AND mc.note = '(1916) (Denmark) (theatrical)' AND mc.note = '(1987) (Japan) (TV)' AND mc.note = '(Televisioteatteri)' AND mi.info = 'Canada:21 October 2003' AND t.production_year < 1978