SELECT MIN(t.title) AS american_movie FROM company_type AS ct, info_type AS it, movie_companies AS mc, movie_info AS mi, title AS t WHERE t.id = mi.movie_id AND t.id = mc.movie_id AND mc.movie_id = mi.movie_id AND ct.id = mc.company_type_id AND it.id = mi.info_type_id AND ct.kind = 'special effects companies' AND mc.note = '(produced with the participation of) (as Ontario Film and Television Tax Credits - Ontario Media Development Corporation)' AND mc.note = '(1997) (USA) (VHS) (on "Bucky Beaver''s Dragon Art Theatre Triple XXX-Rated Double Feature Vol. 29")' AND mi.info = 'RGL 9506' AND t.production_year < 1910