SELECT MIN(t.title) AS american_movie FROM company_type AS ct, info_type AS it, movie_companies AS mc, movie_info AS mi, title AS t WHERE t.id = mi.movie_id AND t.id = mc.movie_id AND mc.movie_id = mi.movie_id AND ct.id = mc.company_type_id AND it.id = mi.info_type_id AND ct.kind = 'special effects companies' AND mc.note = '(1985) (Australia) (theatrical)' AND mc.note = '(2013) (Netherlands) (TV) (HBO3) (limited)' AND mi.info = 'Rod Edgar. "Gate Ahead". In: "Dreamwatch 10pp" (USA), Titan Publishing Group, Iss. 97, (MG)' AND t.production_year < 1960