SELECT MIN(t.title) AS american_movie FROM company_type AS ct, info_type AS it, movie_companies AS mc, movie_info AS mi, title AS t WHERE t.id = mi.movie_id AND t.id = mc.movie_id AND mc.movie_id = mi.movie_id AND ct.id = mc.company_type_id AND it.id = mi.info_type_id AND ct.kind = 'special effects companies' AND mc.note = '(international)' AND mc.note = '(1982) (UK) (VHS)' AND mi.info = 'UK:30 May 1960' AND t.production_year < 1978