SELECT MIN(an1.name) AS actress_pseudonym, MIN(t.title) AS japanese_movie_dubbed FROM aka_name AS an1, cast_info AS ci, company_name AS cn, movie_companies AS mc, name AS n1, role_type AS rt, title AS t WHERE an1.person_id = n1.id AND n1.id = ci.person_id AND ci.movie_id = t.id AND t.id = mc.movie_id AND mc.company_id = cn.id AND ci.role_id = rt.id AND an1.person_id = ci.person_id AND ci.movie_id = mc.movie_id AND ci.note = '(series director of photography)' AND cn.country_code = '[br]' AND mc.note = '(1994) (Canada) (VHS)' AND mc.note = '(1985) (UK) (VHS)' AND n1.name = 'Matthee, Debbie' AND n1.name = 'Matthee, Debbie' AND rt.role = 'production designer'